CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_GetNewProductCodeSKU (IN $inputText varchar(255))
COMMENT 'thủ tục lấy mã hàng hóa mới'
BEGIN
  -- lấy ra tất cả bản ghi có mã bắt đầu giống mã truyền vào từ param
  SET @str = $inputText;
  SET @length = LENGTH(@str) + 1; -- độ dài chuỗi mã

  SET @maxCode = (SELECT
      MAX(tmp3.IsNumber)
    FROM ((SELECT
        (CASE WHEN tmp2.NumberCode REGEXP '^[0-9]+$' THEN CAST(tmp2.NumberCode AS UNSIGNED integer) ELSE 0 END) AS IsNumber
      FROM (SELECT
          SUBSTRING(tmp.ProductCodeSKU, @length, LengthCode) AS NumberCode
        FROM (SELECT
            LENGTH(p.ProductCodeSKU) AS LengthCode,
            p.*
          FROM product p
          WHERE p.ProductCodeSKU LIKE CONCAT(@str, '%')) AS tmp) AS tmp2) AS tmp3));

  SET @maxCode = CAST(@maxCode + 1 AS UNSIGNED integer);
  SET @NewProductCodeSKU = CASE WHEN @maxCode IS NULL THEN CONCAT(@str, '0001') WHEN @maxCode < 10 THEN CONCAT(@str, '000', @maxCode) WHEN @maxCode < 100 THEN CONCAT(@str, '00', @maxCode) WHEN @maxCode < 1000 THEN CONCAT(@str, '0', @maxCode) WHEN @maxCode < 100000000 THEN CONCAT(@str, @maxCode) END;
  SELECT
    @NewProductCodeSKU;

END
$$

--
