CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_FilterProducts (IN $Columns varchar(255),
IN $PageIndex int,
IN $PageSize int,
IN $Where text,
IN $Sort text,
OUT $TotalRecord int,
OUT $TotalPage int)
COMMENT 'thủ tục phân trang và filter dữ liệu hàng hóa'
BEGIN

  IF IFNULL($Columns, '') = '' THEN
    SET $Columns = ' * ';
  END IF;

  IF $PageIndex < 1 THEN
    SET $PageIndex = 1;
  END IF;


  IF $PageSize < 1 THEN
    SET $PageSize = 50;
  END IF;

  IF IFNULL($Where, '') = '' THEN
    SET $Where = ' 1 = 1 ';
  END IF;

  IF IFNULL($Sort, '') = '' THEN
    SET $Sort = ' CreatedDate DESC, ProductCodeSKU DESC, ModifiedDate DESC';
  END IF;

  SET @skip = ($PageIndex - 1) * $PageSize;
  SET @limit = $PageSize;
  SET @where = $Where;
  SET @sort = $Sort;
  SET @column = $Columns;

  SET @sql = CONCAT('SELECT ', @column, ' FROM View_Product WHERE ', @where, ' ORDER BY ', @sort, ' LIMIT ?, ?');
  PREPARE stmt FROM @sql;
  EXECUTE stmt USING @skip, @limit;
  DEALLOCATE PREPARE stmt;

  SET @count = 0;
  SET @sqlCount = CONCAT('SELECT COUNT(ProductId) FROM View_Product WHERE  ', @where, ' INTO @count');
  PREPARE stmtCount FROM @sqlCount;
  EXECUTE stmtCount;
  SET $TotalRecord = @count;

  IF ((@count % $PageSize) > 0) THEN
    SET $TotalPage = FLOOR(@count / $PageSize) + 1;
  ELSE
    SET $TotalPage = FLOOR(@count / $PageSize);
  END IF;
  DEALLOCATE PREPARE stmtCount;




END
$$

